import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
    return (
      <button
        style={props.fontWeight} 
        className="square" 
        onClick={props.onClick}>
        {props.value}
      </button>
    );
}

class Board extends React.Component {
  renderSquare(i) {
    return( 
      <Square 
            value={ this.props.squares[i] }
            style={ this.props.fontWeight } 
            onClick = {() => this.props.onClick(i)}
      />
    );
  }

  render() {
    let board = [];

    for(let r = 0; r < 3; r++ ){
      let row = [];
      for(let c = 0; c < 3; c++){
          row.push(<span key={(r * 3) + c}>{this.renderSquare((r * 3) + c)}</span>);
      }
      board.push(<div className="board-row">{row}</div>);
    }

    return (
      <div>
      {board}
      </div>
      );
    }
  }


class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      history: [{
        squares: Array(9).fill(null),
        moves: Array(2).fill(null),
      }],
      xIsNext: true,
      stepNumber: 0,
      ascending: true,
    };
  }

  handleClick(i) {
    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = current.squares.slice();

    if (calculateWinner(squares) || squares[i]) {
      return;
    }

    squares[i] = this.state.xIsNext ? 'X' : 'O';
    const row = Math.floor(i/3)+1;
    const col = Math.floor(i%3)+1;
    const move = [row, col];

    this.setState({
      history: history.concat([{
        squares: squares,
        moves: move,
      }]),
      xIsNext: !this.state.xIsNext,
      stepNumber: history.length,
    });
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: (step % 2) === 0,
    });

  }

  sortHandleClick(){
    this.setState({
      ascending: !this.state.ascending,
    });

  }

  render() {
    const active = {
      fontWeight: 'bold'
    };

    const inactive = {
      fontWeight: 'normal'
    };

    const history = this.state.history;
    const current = history[this.state.stepNumber];
    const winner = calculateWinner(current.squares);
    const ascending = this.state.ascending;

    const moves = history.map((step, move) => {
      const desc = move ? `Go to move # ${move} -- (${step.moves[0]}, ${step.moves[1]})`: `Go to game start`;
      return (
        <li key={move}> 
          <button style={this.state.stepNumber === move ? active : inactive} onClick = {() => this.jumpTo(move)}>{desc}</button>
        </li>
        );
    });

    let status;
    if (winner) {
      status = 'Winner: ' + winner;
    } else {
      status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
    }


    return (
      <div className="game">
        <div className="game-board">
          <Board
            squares={current.squares}
            onClick={i => this.handleClick(i)}
          />
        </div>
        <div className="game-info">
          <div>{status}</div>
          <ol>{ascending ? moves : moves.reverse()}</ol>
        </div>
        <div className="sort-reverse">
          <button onClick={() => this.sortHandleClick()}>Sort by Desc</button>
        </div>
      </div>
    );
  }
}

function calculateWinner(squares){
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  for (let i = 0; i < lines.length; i++){
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]){
      return squares[a], a, b, c;
    }
  }
  return null;
}
// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
